import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, uuid, boolean, integer, unique, index, uniqueIndex } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  role: text("role").notNull().default("parent"), // parent, teacher, admin
  name: text("name").notNull(),
  email: text("email"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const teachers = pgTable("teachers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id),
  name: text("name").notNull(),
  subject: text("subject").notNull(),
  uniqueCode: text("unique_code").notNull().unique(),
  qrCode: text("qr_code"), // Base64 encoded QR code
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const parentSessions = pgTable("parent_sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: text("session_id").notNull().unique(), // Browser session ID
  parentName: text("parent_name").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const queueEntries = pgTable("queue_entries", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  teacherId: varchar("teacher_id").references(() => teachers.id).notNull(),
  parentSessionId: varchar("parent_session_id").references(() => parentSessions.id).notNull(),
  childName: text("child_name").notNull(),
  status: text("status").notNull().default("waiting"), // waiting, next, current, completed, skipped
  position: integer("position").notNull(),
  joinedAt: timestamp("joined_at").defaultNow(),
  notifiedAt: timestamp("notified_at"),
  startedAt: timestamp("started_at"),
  completedAt: timestamp("completed_at"),
}, (table) => ({
  // Prevent duplicate parent+teacher combinations for ACTIVE statuses only (allow multiple completed/skipped)
  uniqueActiveParentTeacher: uniqueIndex("ux_no_duplicate_active_parent_teacher").on(table.parentSessionId, table.teacherId).where(sql`status IN ('waiting', 'next', 'current')`),
  // Ensure unique positions per teacher for active queue entries
  uniquePositionPerTeacher: uniqueIndex("ux_teacher_position_active").on(table.teacherId, table.position).where(sql`status IN ('waiting', 'next', 'current')`),
  // Ensure only one current status per teacher  
  oneCurrentPerTeacher: uniqueIndex("ux_one_current_per_teacher").on(table.teacherId).where(sql`status = 'current'`),
}));

export const meetings = pgTable("meetings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  teacherId: varchar("teacher_id").references(() => teachers.id).notNull(),
  queueEntryId: varchar("queue_entry_id").references(() => queueEntries.id).notNull(),
  startedAt: timestamp("started_at").defaultNow(),
  endedAt: timestamp("ended_at"),
  duration: integer("duration"), // in seconds
  wasExtended: boolean("was_extended").default(false),
  extendedBy: integer("extended_by").default(0), // in seconds
}, (table) => ({
  // Ensure only one active meeting per teacher
  oneActiveMeetingPerTeacher: uniqueIndex("ux_one_active_meeting_per_teacher").on(table.teacherId).where(sql`ended_at IS NULL`),
}));

// Relations
export const usersRelations = relations(users, ({ one }) => ({
  teacher: one(teachers, {
    fields: [users.id],
    references: [teachers.userId],
  }),
}));

export const teachersRelations = relations(teachers, ({ one, many }) => ({
  user: one(users, {
    fields: [teachers.userId],
    references: [users.id],
  }),
  queueEntries: many(queueEntries),
  meetings: many(meetings),
}));

export const parentSessionsRelations = relations(parentSessions, ({ many }) => ({
  queueEntries: many(queueEntries),
}));

export const queueEntriesRelations = relations(queueEntries, ({ one }) => ({
  teacher: one(teachers, {
    fields: [queueEntries.teacherId],
    references: [teachers.id],
  }),
  parentSession: one(parentSessions, {
    fields: [queueEntries.parentSessionId],
    references: [parentSessions.id],
  }),
  meeting: one(meetings, {
    fields: [queueEntries.id],
    references: [meetings.queueEntryId],
  }),
}));

export const meetingsRelations = relations(meetings, ({ one }) => ({
  teacher: one(teachers, {
    fields: [meetings.teacherId],
    references: [teachers.id],
  }),
  queueEntry: one(queueEntries, {
    fields: [meetings.queueEntryId],
    references: [queueEntries.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

export const insertTeacherSchema = createInsertSchema(teachers).omit({
  id: true,
  userId: true,
  uniqueCode: true,
  qrCode: true,
  createdAt: true,
});

export const insertParentSessionSchema = createInsertSchema(parentSessions).omit({
  id: true,
  createdAt: true,
});

export const insertQueueEntrySchema = createInsertSchema(queueEntries).omit({
  id: true,
  position: true,
  joinedAt: true,
  notifiedAt: true,
  startedAt: true,
  completedAt: true,
});

export const insertMeetingSchema = createInsertSchema(meetings).omit({
  id: true,
  startedAt: true,
  endedAt: true,
  duration: true,
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Teacher = typeof teachers.$inferSelect;
export type InsertTeacher = z.infer<typeof insertTeacherSchema>;
export type ParentSession = typeof parentSessions.$inferSelect;
export type InsertParentSession = z.infer<typeof insertParentSessionSchema>;
export type QueueEntry = typeof queueEntries.$inferSelect;
export type InsertQueueEntry = z.infer<typeof insertQueueEntrySchema>;
export type Meeting = typeof meetings.$inferSelect;
export type InsertMeeting = z.infer<typeof insertMeetingSchema>;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import NotFound from "@/pages/not-found";
import ParentInterface from "@/pages/parent";
import TeacherInterface from "@/pages/teacher";
import AdminInterface from "@/pages/admin";

function Router() {
  return (
    <Switch>
      <Route path="/" component={ParentInterface} />
      <Route path="/parent" component={ParentInterface} />
      <Route path="/teacher" component={TeacherInterface} />
      <Route path="/admin" component={AdminInterface} />
      <Route path="/queue/:code" component={ParentInterface} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

