import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Camera, X } from 'lucide-react';

interface QRScannerProps {
  isOpen: boolean;
  onClose: () => void;
  onScan: (result: string) => void;
}

export default function QRScanner({ isOpen, onClose, onScan }: QRScannerProps) {
  const [isScanning, setIsScanning] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const streamRef = useRef<MediaStream | null>(null);

  const startScanning = async () => {
    try {
      setError(null);
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment' }
      });

      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsScanning(true);
      }
    } catch (err) {
      setError('Camera access denied or not available');
    }
  };

  const stopScanning = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    setIsScanning(false);
  };

  useEffect(() => {
    if (!isOpen) {
      stopScanning();
    }
  }, [isOpen]);

  const handleManualEntry = () => {
    stopScanning();
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md" data-testid="qr-scanner-dialog">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Camera className="h-5 w-5" />
            Scan QR Code
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {!isScanning && !error && (
            <div className="text-center space-y-4">
              <div className="bg-muted rounded-lg p-8">
                <Camera className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                <p className="text-sm text-muted-foreground">
                  Position the QR code within the camera frame
                </p>
              </div>
              <Button 
                onClick={startScanning} 
                className="w-full"
                data-testid="button-start-camera"
              >
                <Camera className="h-4 w-4 mr-2" />
                Start Camera
              </Button>
            </div>
          )}

          {isScanning && (
            <div className="space-y-4">
              <div className="relative">
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  className="w-full rounded-lg"
                  data-testid="camera-video"
                />
                <div className="absolute inset-0 border-2 border-primary rounded-lg pointer-events-none">
                  <div className="absolute top-4 left-4 w-6 h-6 border-l-4 border-t-4 border-primary"></div>
                  <div className="absolute top-4 right-4 w-6 h-6 border-r-4 border-t-4 border-primary"></div>
                  <div className="absolute bottom-4 left-4 w-6 h-6 border-l-4 border-b-4 border-primary"></div>
                  <div className="absolute bottom-4 right-4 w-6 h-6 border-r-4 border-b-4 border-primary"></div>
                </div>
              </div>
              <Button 
                onClick={stopScanning} 
                variant="outline" 
                className="w-full"
                data-testid="button-stop-camera"
              >
                <X className="h-4 w-4 mr-2" />
                Stop Camera
              </Button>
            </div>
          )}

          {error && (
            <div className="text-center space-y-4">
              <div className="bg-destructive/10 text-destructive p-4 rounded-lg">
                <p className="text-sm">{error}</p>
              </div>
              <Button 
                onClick={handleManualEntry} 
                variant="outline" 
                className="w-full"
                data-testid="button-manual-entry"
              >
                Enter Code Manually Instead
              </Button>
            </div>
          )}

          <div className="text-center">
            <Button 
              onClick={onClose} 
              variant="ghost" 
              size="sm"
              data-testid="button-close-scanner"
            >
              Cancel
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/QueueCard.tsx.content:
import { Card, CardContent } from '@/components/ui/card';
import { cn } from '@/lib/utils';

interface QueueCardProps {
  status: 'waiting' | 'next' | 'current' | 'skipped';
  teacherName: string;
  subject: string;
  childName: string;
  className?: string;
}

const statusConfig = {
  waiting: {
    bgClass: 'queue-status-gray',
    title: 'IN QUEUE',
    message: "You're all set. We'll notify you when it's getting close.",
    pulseClass: ''
  },
  next: {
    bgClass: 'queue-status-yellow',
    title: 'GETTING CLOSE',
    message: 'You are next in line. Please start making your way to the classroom.',
    pulseClass: 'notification-badge'
  },
  current: {
    bgClass: 'queue-status-green',
    title: 'YOUR TURN NOW!',
    message: 'The teacher is ready for you now. Please enter the classroom.',
    pulseClass: 'notification-badge'
  },
  skipped: {
    bgClass: 'queue-status-blue',
    title: 'TURN SKIPPED',
    message: 'Your turn was skipped because you were in another meeting. You have priority when your current meeting ends.',
    pulseClass: ''
  }
};

export default function QueueCard({ 
  status, 
  teacherName, 
  subject, 
  childName, 
  className 
}: QueueCardProps) {
  const config = statusConfig[status];

  return (
    <Card className={cn("overflow-hidden", className)} data-testid={`card-queue-${status}`}>
      <div className={cn(config.bgClass, config.pulseClass, "text-white p-4 text-center")}>
        <div className="text-lg font-semibold" data-testid={`text-status-${status}`}>
          {config.title}
        </div>
        <div className="text-sm opacity-90" data-testid={`text-message-${status}`}>
          {config.message}
        </div>
      </div>
      <CardContent className="p-4">
        <div className="flex justify-between items-center mb-2">
          <span className="font-medium text-foreground" data-testid="text-teacher-name">
            {teacherName}
          </span>
          <span className="text-sm text-muted-foreground" data-testid="text-subject">
            {subject}
          </span>
        </div>
        <div className="text-sm text-muted-foreground">
          Student: <span data-testid="text-child-name">{childName}</span>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/TeacherCard.tsx.content:
import { Card, CardContent } from '@/components/ui/card';
import type { Teacher, QueueEntry, Meeting } from '@shared/schema';
import { cn } from '@/lib/utils';

interface TeacherCardProps {
  teacher: Teacher;
  currentMeeting?: Meeting & { queueEntry?: QueueEntry };
  nextParent?: QueueEntry;
  queueSize: number;
}

export default function TeacherCard({ 
  teacher, 
  currentMeeting, 
  nextParent, 
  queueSize
}: TeacherCardProps) {
  const isHighQueue = queueSize >= 6;

  return (
    <Card className={cn(
      "border-border",
      isHighQueue && "border-orange-200 bg-orange-50"
    )} data-testid={`card-teacher-${teacher.id}`}>
      <CardContent className="p-4">
        <div className="flex justify-between items-start mb-3">
          <div>
            <h4 className="font-semibold text-foreground" data-testid="text-teacher-name">
              {teacher.name}
            </h4>
            <p className="text-sm text-muted-foreground" data-testid="text-teacher-details">
              {teacher.subject}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            <span className="text-xs font-medium text-muted-foreground">Code:</span>
            <span className="text-sm font-bold text-primary" data-testid="text-teacher-code">
              {teacher.uniqueCode}
            </span>
            {isHighQueue && (
              <span className="bg-orange-200 text-orange-800 text-xs px-2 py-1 rounded" data-testid="badge-high-queue">
                HIGH QUEUE
              </span>
            )}
          </div>
        </div>

        <div className="grid md:grid-cols-3 gap-4">
          <div className="bg-green-50 border border-green-200 rounded p-3">
            <div className="text-xs text-green-600 font-medium">CURRENT MEETING</div>
            {currentMeeting ? (
              <>
                <div className="text-sm font-semibold text-green-700" data-testid="text-current-parent">
                  {(currentMeeting.queueEntry as any)?.parentSession?.parentName || 'Unknown Parent'}
                </div>
                <div className="text-xs text-green-600">
                  Meeting in progress
                </div>
              </>
            ) : (
              <div className="text-sm text-green-600" data-testid="text-no-meeting">No active meeting</div>
            )}
          </div>

          <div className="bg-yellow-50 border border-yellow-200 rounded p-3">
            <div className="text-xs text-yellow-600 font-medium">NEXT UP</div>
            {nextParent ? (
              <>
                <div className="text-sm font-semibold text-yellow-700" data-testid="text-next-parent">
                  {(nextParent as any)?.parentSession?.parentName || 'Unknown Parent'}
                </div>
                <div className="text-xs text-yellow-600">Ready to go</div>
              </>
            ) : (
              <div className="text-sm text-yellow-600" data-testid="text-no-next">No one waiting</div>
            )}
          </div>

          <div className={cn(
            "rounded p-3",
            isHighQueue ? "bg-red-50 border border-red-200" : "bg-blue-50 border border-blue-200"
          )}>
            <div className={cn(
              "text-xs font-medium",
              isHighQueue ? "text-red-600" : "text-blue-600"
            )}>
              QUEUE SIZE
            </div>
            <div className={cn(
              "text-sm font-semibold",
              isHighQueue ? "text-red-700" : "text-blue-700"
            )} data-testid="text-queue-size">
              {queueSize} waiting
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

