client/src/pages/parent.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { useWebSocket } from '@/hooks/use-websocket';
import QRScanner from '@/components/QRScanner';
import QueueCard from '@/components/QueueCard';
import { apiRequest } from '@/lib/queryClient';
import { Smartphone, Plus, Camera, Hash } from 'lucide-react';

export default function ParentInterface() {
  const [location] = useLocation();
  // CRITICAL FIX: Use state for sessionId so we can update it with server-returned sessionId
  const [sessionId, setSessionId] = useState(() => {
    // First check for server session ID
    const serverSessionId = localStorage.getItem('ptm_server_session');
    if (serverSessionId) {
      return serverSessionId;
    }

    // Fallback to device session for initial creation
    let deviceSessionId = localStorage.getItem('ptm_device_session');
    if (!deviceSessionId) {
      // Create a device-specific session ID for initial request
      deviceSessionId = `device-${Date.now()}-${crypto.randomUUID()}`;
      localStorage.setItem('ptm_device_session', deviceSessionId);
    }
    return deviceSessionId;
  });
  const [currentStep, setCurrentStep] = useState<'welcome' | 'join' | 'child-info' | 'dashboard'>('welcome');
  const [showQRScanner, setShowQRScanner] = useState(false);
  const [selectedTeacher, setSelectedTeacher] = useState<any>(null);
  const [parentName, setParentName] = useState('');
  const [childName, setChildName] = useState('');
  const [childGrade, setChildGrade] = useState('Not specified');
  const [teacherCode, setTeacherCode] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Check if coming from QR code URL
  useEffect(() => {
    const pathMatch = location.match(/^\/queue\/(.+)$/);
    if (pathMatch) {
      const code = pathMatch[1];
      setTeacherCode(code.toUpperCase());
      handleTeacherCodeSubmit(code.toUpperCase());
    }
  }, [location]);


  // Check if parent session exists
  const { data: parentSession } = useQuery<{id: string, sessionId: string, parentName: string} | null>({
    queryKey: ['/api/parent/session', sessionId],
    enabled: !!sessionId
  });

  // Get parent's queues - using sessionId not parentSession.id
  const { data: allQueues = [], refetch: refetchQueues } = useQuery({
    queryKey: [`/api/parent/${sessionId}/queues`],
    enabled: !!parentSession && !!sessionId
  });

  // Filter to show active queues (not completed)
  const parentQueues = Array.isArray(allQueues) ? allQueues.filter((queue: any) =>
    queue.status === 'waiting' || queue.status === 'next' || queue.status === 'current' || queue.status === 'skipped'
  ) : [];

  // WebSocket for real-time updates
  const { lastMessage } = useWebSocket({
    sessionId,
    userType: 'parent',
    parentSessionId: parentSession?.id || sessionId
  });

  // Handle WebSocket messages
  useEffect(() => {
    if (lastMessage) {
      if (lastMessage.type === 'status_update' || lastMessage.type === 'queue_update') {
        refetchQueues();
        if (lastMessage.message) {
          toast({
            title: lastMessage.message,
            description: lastMessage.description || 'Your queue status has been updated',
            duration: 5000
          });
        }
      } else if (lastMessage.type === 'delay_notification') {
        toast({
          title: 'Meeting Delayed',
          description: lastMessage.message,
          duration: 5000
        });
      } else if (lastMessage.type === 'queue_removed') {
        refetchQueues();
        toast({
          title: 'Queue Update',
          description: 'You have been removed from the queue',
          duration: 5000
        });
      }
    }
  }, [lastMessage, refetchQueues, toast]);

  // Auto-refresh queue data every 30 seconds to prevent stale data
  useEffect(() => {
    const interval = setInterval(() => {
      refetchQueues();
    }, 30000);
    return () => clearInterval(interval);
  }, [refetchQueues]);

  // Create parent session mutation
  const createSessionMutation = useMutation({
    mutationFn: async (name: string) => {
      const response = await apiRequest('POST', '/api/parent/session', {
        sessionId,
        parentName: name
      });
      return response.json();
    },
    onSuccess: (data) => {
      // CRITICAL FIX: Update sessionId with server-returned sessionId
      console.log('Session creation successful:', { 
        serverReturned: data.sessionId, 
        currentClientSessionId: sessionId,
        fullResponse: data 
      });
      if (data.sessionId && data.sessionId !== sessionId) {
        console.log('Updating client sessionId from', sessionId, 'to', data.sessionId);
        setSessionId(data.sessionId);
        localStorage.setItem('ptm_server_session', data.sessionId);
        // Remove old device session since we now have server session
        localStorage.removeItem('ptm_device_session');
        console.log('Updated localStorage:', {
          serverSession: localStorage.getItem('ptm_server_session'),
          deviceSession: localStorage.getItem('ptm_device_session')
        });
      } else {
        console.log('No sessionId update needed or sessionId missing from response');
      }
      setCurrentStep('join');
      // Invalidate with new sessionId
      queryClient.invalidateQueries({ queryKey: ['/api/parent/session', data.sessionId || sessionId] });
    }
  });

  // Join queue mutation
  const joinQueueMutation = useMutation({
    mutationFn: async (data: { teacherCode: string; childName: string }) => {
      console.log('Joining queue with data:', { sessionId, ...data });
      const response = await apiRequest('POST', '/api/parent/join-queue', {
        sessionId,
        ...data
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to join queue');
      }
      return response.json();
    },
    onSuccess: () => {
      setCurrentStep('dashboard');
      setChildName('');
      setTeacherCode('');
      setSelectedTeacher(null);
      refetchQueues();
      toast({
        title: 'Successfully joined queue',
        description: 'You\'ll be notified when it\'s your turn'
      });
    },
    onError: (error) => {
      console.log('Join queue error:', error.message);
      if (error.message.includes('already in this teacher\'s queue')) {
        // Parent is already in queue, just go to dashboard
        setCurrentStep('dashboard');
        refetchQueues();
        toast({
          title: 'Already in queue',
          description: 'You\'re already in this teacher\'s queue',
        });
      } else {
        toast({
          title: 'Error joining queue',
          description: error.message,
          variant: 'destructive'
        });
      }
    }
  });

  // Set initial step based on session (only on first load)
  useEffect(() => {
    if (parentSession && currentStep === 'welcome') {
      setCurrentStep(parentQueues.length > 0 ? 'dashboard' : 'join');
    }
  }, [parentSession, parentQueues, currentStep]);

  const handleParentNameSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (parentName.trim()) {
      createSessionMutation.mutate(parentName.trim());
    }
  };

  const handleTeacherCodeSubmit = async (code: string) => {
    try {
      console.log('Looking up teacher with code:', code);
      const response = await fetch(`/api/teachers/by-code/${code}`);
      if (!response.ok) {
        throw new Error('Teacher not found');
      }
      const teacher = await response.json();
      console.log('Teacher found:', teacher);
      setSelectedTeacher(teacher);
      setCurrentStep('child-info');
    } catch (error) {
      console.log('Teacher lookup failed:', error);
      toast({
        title: 'Invalid code',
        description: 'Please check the teacher code and try again',
        variant: 'destructive'
      });
    }
  };

  const handleQRScan = (result: string) => {
    try {
      const qrData = JSON.parse(result);
      if (qrData.type === 'teacher_queue' && qrData.code) {
        handleTeacherCodeSubmit(qrData.code);
        setShowQRScanner(false);
      }
    } catch (error) {
      toast({
        title: 'Invalid QR code',
        description: 'Please scan a valid teacher QR code',
        variant: 'destructive'
      });
    }
  };

  const handleChildInfoSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Child info form submitted:', { childName, selectedTeacher });

    if (!childName.trim()) {
      console.log('Child name is empty');
      toast({
        title: 'Missing Information',
        description: 'Please enter your child\'s name',
        variant: 'destructive'
      });
      return;
    }

    if (!selectedTeacher) {
      console.log('No teacher selected');
      toast({
        title: 'No Teacher Selected',
        description: 'Please go back and select a teacher',
        variant: 'destructive'
      });
      return;
    }

    console.log('Calling join queue mutation with:', {
      teacherCode: selectedTeacher.uniqueCode,
      childName: childName.trim()
    });

    joinQueueMutation.mutate({
      teacherCode: selectedTeacher.uniqueCode,
      childName: childName.trim()
    });
  };

  const handleManualCodeSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (teacherCode.trim()) {
      handleTeacherCodeSubmit(teacherCode.trim().toUpperCase());
    }
  };

  if (currentStep === 'welcome') {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-md mx-auto space-y-6">
          <Card className="shadow-lg" data-testid="card-welcome">
            <CardContent className="p-6 text-center">
              <div className="mb-6">
                <Smartphone className="text-primary text-4xl mb-3 mx-auto" />
                <h2 className="text-2xl font-semibold text-foreground mb-2">Welcome to PTM</h2>
                <p className="text-muted-foreground">Let's get you set up for today's parent-teacher meetings</p>
              </div>
              <form onSubmit={handleParentNameSubmit} className="space-y-4">
                <div>
                  <Label htmlFor="parent-name" className="block text-sm font-medium text-foreground mb-2">
                    Your Name
                  </Label>
                  <Input
                    id="parent-name"
                    type="text"
                    value={parentName}
                    onChange={(e) => setParentName(e.target.value)}
                    placeholder="Enter your name"
                    required
                    data-testid="input-parent-name"
                  />
                </div>
                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={createSessionMutation.isPending}
                  data-testid="button-get-started"
                >
                  {createSessionMutation.isPending ? 'Setting up...' : 'Get Started'}
                </Button>
              </form>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (currentStep === 'join') {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-md mx-auto space-y-6">
          <Card className="shadow-lg" data-testid="card-join-queue">
            <CardContent className="p-6">
              <div className="text-center mb-6">
                <h2 className="text-xl font-semibold text-foreground mb-2">Join a Teacher's Queue</h2>
                <p className="text-muted-foreground">Scan the QR code or enter the teacher's unique code</p>
              </div>

              {/* QR Scanner */}
              <div className="bg-muted rounded-lg p-6 text-center mb-4">
                <Camera className="text-4xl text-muted-foreground mb-3 mx-auto" />
                <p className="text-sm text-muted-foreground mb-3">Point your camera at the QR code</p>
                <Button 
                  onClick={() => setShowQRScanner(true)}
                  data-testid="button-scan-qr"
                >
                  <Camera className="mr-2 h-4 w-4" />
                  Scan QR Code
                </Button>
              </div>

              {/* Manual Code Entry */}
              <div className="border-t border-border pt-4">
                <p className="text-center text-muted-foreground mb-3">or enter the code manually</p>
                <form onSubmit={handleManualCodeSubmit} className="space-y-3">
                  <Input
                    type="text"
                    value={teacherCode}
                    onChange={(e) => setTeacherCode(e.target.value.toUpperCase())}
                    placeholder="e.g. JONES7"
                    className="text-center uppercase"
                    data-testid="input-teacher-code"
                  />
                  <Button 
                    type="submit" 
                    variant="secondary" 
                    className="w-full"
                    data-testid="button-join-queue"
                  >
                    <Hash className="mr-2 h-4 w-4" />
                    Join Queue
                  </Button>
                </form>
              </div>
            </CardContent>
          </Card>

          <QRScanner
            isOpen={showQRScanner}
            onClose={() => setShowQRScanner(false)}
            onScan={handleQRScan}
          />
        </div>
      </div>
    );
  }

  if (currentStep === 'child-info' && selectedTeacher) {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-md mx-auto space-y-6">
          <Card className="shadow-lg" data-testid="card-child-info">
            <CardContent className="p-6">
              <h2 className="text-xl font-semibold text-foreground mb-4">Child Information</h2>
              <p className="text-muted-foreground mb-6">
                Please provide your child's details for{' '}
                <span className="font-medium text-foreground" data-testid="text-selected-teacher">
                  {selectedTeacher.name}
                </span>
              </p>

              <form onSubmit={handleChildInfoSubmit} className="space-y-4">
                <div>
                  <Label htmlFor="child-name" className="block text-sm font-medium text-foreground mb-2">
                    Child's Name
                  </Label>
                  <Input
                    id="child-name"
                    type="text"
                    value={childName}
                    onChange={(e) => setChildName(e.target.value)}
                    placeholder="Enter child's name"
                    required
                    data-testid="input-child-name"
                  />
                </div>
                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={joinQueueMutation.isPending}
                  data-testid="button-join-teacher-queue"
                >
                  {joinQueueMutation.isPending ? 'Joining...' : 'Join Queue'}
                </Button>
              </form>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-md mx-auto space-y-6">
        <div className="text-center mb-6">
          <h2 className="text-xl font-semibold text-foreground">Your Queues</h2>
          <p className="text-muted-foreground">We'll notify you when it's time</p>
        </div>

        <div className="space-y-4" data-testid="container-queue-list">
          {parentQueues.map((queue: any) => (
            <QueueCard
              key={queue.id}
              status={queue.status as 'waiting' | 'next' | 'current' | 'skipped'}
              teacherName={queue.teacher?.name || 'Teacher'}
              subject={queue.teacher?.subject || ''}
              childName={queue.childName}
            />
          ))}
        </div>

        <Button 
          onClick={() => setCurrentStep('join')} 
          variant="secondary" 
          className="w-full"
          data-testid="button-join-another"
        >
          <Plus className="mr-2 h-4 w-4" />
          Join Another Queue
        </Button>
      </div>
    </div>
  );
}

client/src/pages/teacher.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { useWebSocket } from '@/hooks/use-websocket';
import { apiRequest } from '@/lib/queryClient';
import { Presentation, Check, UserX } from 'lucide-react';

export default function TeacherInterface() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [teacherId, setTeacherId] = useState<string | null>(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Get teacher data after login
  const loginMutation = useMutation({
    mutationFn: async (credentials: { email: string; password: string }) => {
      const response = await apiRequest('POST', '/api/teacher/login', credentials);
      return response.json();
    },
    onSuccess: (data) => {
      setIsLoggedIn(true);
      setTeacherId(data.teacher.id);
    },
    onError: () => {
      toast({
        title: 'Invalid credentials',
        description: 'Please check your email and password',
        variant: 'destructive'
      });
    }
  });

  // Get teacher data
  const { data: teacher } = useQuery({
    queryKey: ['/api/teacher', teacherId],
    enabled: !!teacherId && isLoggedIn
  });

  // Get teacher's queue
  const { data: queue = [], refetch: refetchQueue } = useQuery({
    queryKey: ['/api/teacher', teacherId, 'queue'],
    enabled: !!teacherId && isLoggedIn
  });

  // Get current meeting
  const { data: currentMeeting, refetch: refetchMeeting } = useQuery({
    queryKey: ['/api/teacher', teacherId, 'current-meeting'],
    enabled: !!teacherId && isLoggedIn
  });

  // WebSocket for real-time updates
  const { lastMessage } = useWebSocket({
    userType: 'teacher',
    teacherId: teacherId || undefined
  });

  // Handle WebSocket messages
  useEffect(() => {
    if (lastMessage) {
      if (lastMessage.type === 'queue_update' || lastMessage.type === 'meeting_ended') {
        refetchQueue();
        refetchMeeting();
      }
    }
  }, [lastMessage, refetchQueue, refetchMeeting]);


  // End meeting mutation
  const endMeetingMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', `/api/teacher/${teacherId}/end-meeting`);
      return response.json();
    },
    onSuccess: () => {
      refetchQueue();
      refetchMeeting();
      toast({
        title: 'Meeting ended',
        description: 'Next parent has been notified'
      });
    }
  });


  // Skip no-show mutation
  const skipNoShowMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', `/api/teacher/${teacherId}/skip-no-show`);
      return response.json();
    },
    onSuccess: () => {
      refetchQueue();
      refetchMeeting();
      toast({
        title: 'Parent skipped',
        description: 'Next parent has been notified'
      });
    }
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (email && password) {
      loginMutation.mutate({ email, password });
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setTeacherId(null);
    setEmail('');
    setPassword('');
    toast({
      title: 'Logged out',
      description: 'You have been successfully logged out'
    });
  };


  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="w-full max-w-md shadow-lg" data-testid="card-teacher-login">
          <CardContent className="p-6">
            <div className="text-center mb-6">
              <Presentation className="text-primary text-4xl mb-3 mx-auto" />
              <h2 className="text-2xl font-semibold text-foreground">Teacher Login</h2>
              <p className="text-muted-foreground">Access your queue dashboard</p>
            </div>
            <form onSubmit={handleLogin} className="space-y-4">
              <div>
                <Label htmlFor="teacher-email">Email</Label>
                <Input
                  id="teacher-email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="teacher@school.edu"
                  required
                  data-testid="input-teacher-email"
                />
              </div>
              <div>
                <Label htmlFor="teacher-password">Password</Label>
                <Input
                  id="teacher-password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter password"
                  required
                  data-testid="input-teacher-password"
                />
              </div>
              <Button type="submit" className="w-full" data-testid="button-teacher-login">
                Sign In
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  const queueArray = Array.isArray(queue) ? queue : [];
  const currentParent = queueArray.find((entry: any) => entry.status === 'current');
  const nextParent = queueArray.find((entry: any) => entry.status === 'next') || queueArray.find((entry: any) => entry.status === 'waiting');
  const waitingParents = queueArray.filter((entry: any) => entry.status === 'waiting' && entry.id !== nextParent?.id);

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Header */}
        <Card className="shadow-lg" data-testid="card-teacher-header">
          <CardContent className="p-6">
            <div className="flex justify-between items-center">
              <div>
                <h2 className="text-2xl font-semibold text-foreground" data-testid="text-teacher-name">
                  {(teacher as any)?.name || 'Loading...'}
                </h2>
                <p className="text-muted-foreground" data-testid="text-teacher-subject">
                  {(teacher as any)?.subject || ''}
                </p>
              </div>
              <div className="flex items-center space-x-4">
                <div className="text-right">
                  <div className="text-sm text-muted-foreground">Queue Code</div>
                  <div className="text-xl font-bold text-primary" data-testid="text-queue-code">
                    {(teacher as any)?.uniqueCode || ''}
                  </div>
                </div>
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={handleLogout}
                  className="text-muted-foreground hover:text-foreground"
                  data-testid="button-teacher-logout"
                >
                  <svg className="mr-2 h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                  </svg>
                  Log Out
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Current Meeting */}
          <Card className="shadow-lg" data-testid="card-current-meeting">
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold text-foreground mb-4">Current Meeting</h3>
              <div className="text-center space-y-4">
                {currentMeeting ? (
                  <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                    <div className="text-2xl font-semibold text-green-700" data-testid="text-current-parent">
                      {(currentMeeting as any)?.queueEntry?.parentName || 'Unknown Parent'}
                    </div>
                    <div className="text-green-600" data-testid="text-current-student">
                      Student: {(currentMeeting as any)?.queueEntry?.childName || 'Unknown Student'}
                    </div>
                  </div>
                ) : (
                  <div className="bg-muted rounded-lg p-4">
                    <div className="text-muted-foreground" data-testid="text-no-current-meeting">
                      No active meeting
                    </div>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Queue Management */}
          <Card className="shadow-lg" data-testid="card-queue-management">
            <CardContent className="p-6">
              <h3 className="text-lg font-semibold text-foreground mb-4">Up Next & Queue</h3>

              {/* Next Parent */}
              {nextParent && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mb-4">
                  <div className="text-sm text-yellow-600 font-medium">NEXT</div>
                  <div className="font-semibold text-yellow-700" data-testid="text-next-parent-name">
                    {nextParent.parentSession?.parentName || 'Unknown Parent'}
                  </div>
                  <div className="text-sm text-yellow-600">
                    Student: <span data-testid="text-next-child">{nextParent.childName}</span>
                  </div>
                </div>
              )}

              {/* Waiting List */}
              <div className="space-y-2">
                <div className="text-sm font-medium text-muted-foreground">
                  Waiting ({waitingParents.length})
                </div>
                <div className="space-y-2" data-testid="container-waiting-list">
                  {waitingParents.map((parent: any, index: number) => (
                    <div key={parent.id} className="flex justify-between items-center py-2 px-3 bg-muted rounded-md">
                      <div>
                        <div className="text-sm font-medium text-foreground" data-testid={`text-waiting-parent-${index}`}>
                          {parent.parentSession?.parentName || 'Unknown Parent'}
                        </div>
                        <div className="text-xs text-muted-foreground" data-testid={`text-waiting-student-${index}`}>
                          Student: {parent.childName}
                        </div>
                      </div>
                      <div className="text-xs text-muted-foreground" data-testid={`text-wait-time-${index}`}>
                        Position {parent.position}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Control Buttons */}
        <Card className="shadow-lg" data-testid="card-meeting-controls">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-foreground mb-4">Meeting Controls</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Button 
                onClick={() => endMeetingMutation.mutate()}
                disabled={endMeetingMutation.isPending || skipNoShowMutation.isPending || !currentMeeting}
                className="py-3 px-6 flex items-center justify-center"
                data-testid="button-end-meeting"
              >
                <Check className="mr-2 h-4 w-4" />
                {endMeetingMutation.isPending ? 'Ending...' : 'End Meeting'}
              </Button>
              <Button 
                onClick={() => skipNoShowMutation.mutate()}
                disabled={skipNoShowMutation.isPending || endMeetingMutation.isPending || !currentMeeting}
                variant="destructive"
                className="py-3 px-6 flex items-center justify-center"
                data-testid="button-skip-no-show"
              >
                <UserX className="mr-2 h-4 w-4" />
                {skipNoShowMutation.isPending ? 'Skipping...' : 'Skip / No-Show'}
              </Button>
            </div>
            <div className="mt-4 text-sm text-muted-foreground text-center">
              <i className="fas fa-info-circle mr-1"></i>
              The system automatically handles conflicts and notifications
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

